
public class edu_areashapes {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
//		#area of rectangle = length * height
		int l,h,rec;
		l = 10;
		h = 6;
		rec  = l * h;
		System.out.println("area of rectangle : " + rec );
		
//		#area of square = a rest to 2
		int a, res;
		a = 4;
		res = a*a;
		System.out.println("area of square : " + res);
		
//		area of circle = pi * r rest to 2
		float pi , r, result;
		pi = 3.14f;
		r = 3;
		result = pi*(r*r);
		System.out.println("area of circle : " + result);
	}

}
